{% set blogChildList = module('V2_Blog_ChildList', null, {
  bid: null,
  keyword: null,
  field: null,
}) %}

{% if blogChildList.items is not empty %}
  <ul class="acms-list-group">
    {{ include('/admin/module/setting.twig', { moduleInfo: blogChildList.moduleInfo }) }}
    {% for item in blogChildList.items %}
      <li>
        <a href="{{ item.url }}" class="acms-list-group-item">{{ item.name }}</a>
      </li>
    {% endfor %}
  </ul>
{% endif %}
