{% set blogGeoList = module('V2_Blog_GeoList', null, {
  bid: null,
  keyword: null,
  field: null,
}) %}

{% if blogGeoList.items is not empty %}
  <ul class="acms-list-group">
    {{ include('/admin/module/setting.twig', { moduleInfo: blogChildList.moduleInfo }) }}
    {% for item in blogGeoList.items %}
      <li>
        <a href="{{ item.url }}" class="acms-list-group-item">{{ item.name }}（{{ item.geo.distance | number_format }}m）</a>
      </li>
    {% endfor %}
  </ul>
{% endif %}
