{% set categoryGeoList = module('V2_Category_GeoList', null, {
  bid: null,
  cid: null,
  page: PAGE,
  limit: null,
  keyword: null,
  field: null,
}) %}

{% if categoryGeoList.items is not empty %}
<div class="acms-margin-bottom-medium">
  {{ include('/admin/module/setting.twig', { moduleInfo: categoryGeoList.moduleInfo }) }}

  {% for item in categoryGeoList.items %}
  <div class="acms-summary">
    <div class="acms-summary-body">
      <h4 class="acms-summary-heading">{{ item.name }}</h4>
      <p>{{ item.geo.distance | number_format }}m</p>
    </div>
  </div>
  {% endfor %}

  <!-- ページネーション -->
  {{
    include('/include/pagination.twig', {
      pagination: categoryGeoList.pagination,
    })
  }}
</div>
{% endif %}
