{% set entryArchiveList = module('V2_Entry_ArchiveList', null, { bid: null, cid: null, limit: null, keyword: null, tag: [], field: null }) %}

<section class="acms-margin-bottom-medium">
  {{ include('/admin/module/setting.twig', { moduleInfo: entryArchiveList.moduleInfo }) }}
  <ul class="acms-list-group">
    {% for archive in entryArchiveList.items %}
      <li>
        <a href="{{ archive.url }}" class="acms-list-group-item">
          {{ archive.date|date('Y年m月') }}
          {% if archive.amount %}
            <span class="acms-badge acms-float-right">{{ archive.amount }}</span>
          {% endif %}
        </a>
      </li>
    {% endfor %}
  </ul>
</section>
