{% set fieldValueList = module('V2_Field_ValueList', null, {bid: null, limit: null, field: null}) %}

<div class="acms-form acms-margin-bottom-medium">
  {{ include('/admin/module/setting.twig', { moduleInfo: fieldValueList.moduleInfo }) }}
  <select>
    {% for item in fieldValueList.items %}
      <option value="{{ item }}">{{ item }}</option>
    {% endfor %}
  </select>
</div>
