{% set links = module('V2_Links', '') %}

{% if links.items is not empty %}
  <div class="acms-margin-bottom-medium">
    {{ include('/admin/module/setting.twig', { moduleInfo: links.moduleInfo }) }}
    <ul class="acms-list-group">
      {% for link in links.items %}
        <li>
          <a href="{{ linl.url }}" class="acms-list-group-item">{{ link.name }}</a>
        </li>
      {% endfor %}
    </ul>
  </div>
{% endif %}
