{% set sitemap = module('V2_Sitemap', null) %}
<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
  {% for item in sitemap.items %}
    <url>
      <loc>{{ item.loc }}</loc>
      {% if item.lastmod %}
      <lastmod>{{ item.lastmod }}</lastmod>
      {% endif %}
    </url>
  {% endfor %}
</urlset>
