{% set userGeoList = module('V2_User_GeoList', null, {
  bid: null,
  uid: null,
  page: PAGE,
  limit: null,
  keyword: null,
  field: null,
}) %}

{% if userGeoList.items is not empty %}
<div class="acms-margin-bottom-medium">
  {{ include('/admin/module/setting.twig', { moduleInfo: userGeoList.moduleInfo }) }}

  {% for item in userGeoList.items %}
  <div class="acms-summary">
    {% if item.icon %}
    <div class="acms-summary-image">
      <img src="{{ item.icon }}" alt="{{ item.name }}" width="60" />
    </div>
    {% endif %}
    <div class="acms-summary-body">
      <h4 class="acms-summary-heading">{{ item.name }}</h4>
      <p>{{ item.geo.distance | number_format }}m</p>
    </div>
  </div>
  {% endfor %}

  <!-- ページネーション -->
  {{
    include('/include/pagination.twig', {
      pagination: userGeoList.pagination,
    })
  }}
</div>
{% endif %}
